<?phpfunction getFilePackagesContent($filename) {    $o = file_get_contents($filename);    $o = trim(str_replace(array('<?php','<?','?>'),'',$o));    return $o;}$plugin = $modx->newObject('modPlugin');$plugin -> fromArray(array(    'id' => 1,    'category' => 0,    'name' => PKG_NAME,    'description' => 'New version of great plugin for expand pictures similar to Lightbox. The following viewers can be selected Highslide, Colorbox and prettyPhoto.',    'plugincode' => getFilePackagesContent($sources['elements'].'plugins/plugin.directresize2.php'),),'',true,true);// $properties = include $sources['data'].'properties/properties.resizeimageonupload.php';// вариант при получении параметров из экспортного файла$properties = file_get_contents($sources['data'].'properties/properties.directresize2.js');$plugin->setProperties(json_decode($properties,true)); // принимает только массив, вполне подходят экспортные параметры в UTF8-without BOMunset($properties);return $plugin;